mapper.select:
SELECT rnum, gno, content, regdate, name
FROM (
	SELECT ROWNUM as rnum, gno, content, regdate, name
	FROM (
		SELECT gno, content, regdate, name
		FROM guestbook
		ORDER BY gno DESC
	)
	WHERE rownum <= #{endRowNo}
)
WHERE rnum >= #{startRowNo}

mapper.update:
UPDATE guestbook
SET
content=#{content}
WHERE gno=#{gno}
